а<template>
  <span :class="badgeClass">
   <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
<mask id="mask0_1_8879" style="mask-type:alpha" maskUnits="userSpaceOnUse" x="0" y="0" width="24" height="24">
<rect width="24" height="24" fill="url(#pattern0_1_8879)"/>
</mask>
<g mask="url(#mask0_1_8879)">
<rect x="-8.51611" y="-9" width="39.4839" height="44.9032" fill="#3B4EFC"/>
</g>
<defs>
<pattern id="pattern0_1_8879" patternContentUnits="objectBoundingBox" width="1" height="1">
<use xlink:href="#image0_1_8879" transform="scale(0.00195312)"/>
</pattern>
<image id="image0_1_8879" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
</defs>
</svg>
    {{ displayText }}
  </span>
</template>

<script setup>
import { ref, computed, onMounted, onUnmounted } from 'vue'

const props = defineProps({
  seconds: {
    type: Number,
    required: true
  }
})

const currentDiff = ref(0)
const initialSeconds = ref(0)
const startTime = ref(0)
let intervalId = null

const isPast = computed(() => currentDiff.value < 0)

const badgeClass = computed(() => {
  const baseClass = 'px-3 py-1 rounded-full text-sm font-medium inline-block'
  if (isPast.value) {
    return `${baseClass} bg-cabinet-green/20 text-black`
  }
  return `${baseClass} bg-cabinet-orange/20 text-black`
})

const displayText = computed(() => {
  const absDiff = Math.abs(currentDiff.value)

  const days = Math.floor(absDiff / 86400)
  const hours = Math.floor((absDiff % 86400) / 3600)
  const minutes = Math.floor((absDiff % 3600) / 60)

  // Если время истекло (в прошлом)
  if (isPast.value) {
    // Показываем сколько времени прошло с момента окончания
    if (absDiff >= 86400) {
      return `${days}D ago`
    } else if (absDiff >= 3600) {
      return `${hours}H ago`
    } else if (absDiff >= 60) {
      return `${minutes}M ago`
    } else {
      return 'Just now'
    }
  }

  // Если время еще не истекло - показываем оставшееся время
  if (absDiff >= 86400) {
    return `${days}D ${hours}H left`
  } else if (absDiff >= 3600) {
    return `${hours}H ${minutes}M left`
  } else if (absDiff >= 60) {
    return `${minutes}M left`
  } else {
    return '< 1M left'
  }
})

function updateTimer() {
  // Вычисляем сколько секунд прошло с момента монтирования
  const elapsed = Math.floor((Date.now() - startTime.value) / 1000)
  // Вычитаем из исходных секунд
  currentDiff.value = initialSeconds.value - elapsed
}

onMounted(() => {
  // Запоминаем исходное значение секунд с сервера
  initialSeconds.value = props.seconds
  currentDiff.value = props.seconds
  startTime.value = Date.now()

  intervalId = setInterval(updateTimer, 1000)
})

onUnmounted(() => {
  if (intervalId) {
    clearInterval(intervalId)
  }
})
</script>
